sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	$rootScope.$on('updateNoteList',function(){
		c.server.get().then(function(r){
			c.data.notes = r.data.notes;
		});
	})
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	var data_notes=[];
	var notesGR = new GlideRecord("x_snc_createnotes_note");
	notesGR.query();
	var myNote ={};
	while(notesGR.next()){
		myNote ={};
		//$sp.getRecordDisplayValues(myNote, notesGR, 'number,title,sys_id');
		myNote.sys_id =notesGR.getValue("sys_id");
		myNote.title = notesGR.getValue('title');
		myNote.note = notesGR.getValue('note');
		data_notes.push(myNote);
	}
	data.notes = data_notes;

})();
